import axios from 'axios';

// הגדרת כתובת ברירת מחדל
const apiClient = axios.create({
    baseURL: 'https://localhost:5016', // הכנס את ה-API שלך כאן
    timeout: 1000, // זמן המתנה לבקשות
});
apiClient.interceptors.response.use(
    response => {
        // אם הבקשה הצליחה, מחזירים את ה-response
        return response;
    },
    error => {
        // כאן תופסים את השגיאות
        console.error('Error response:', error.response ? error.response.data : error.message);
        return Promise.reject(error);
    }
);


// ניתן להוסיף כאן גם הגדרות נוספות כמו headers
apiClient.defaults.headers.common['Authorization'] = 'Bearer token';

export default apiClient;
